scripts.build:
tsup src/index.ts src/cli/index.ts --format esm --clean --sourcemap && tsc --emitDeclarationOnly --declaration --declarationMap -p tsconfig.json && node --input-type=module -e "import { copyFileSync, existsSync } from 'node:fs';
import { resolve } from 'node:path';
const dist = resolve('dist');
const pairs = [
  ['index.js', 'opendevbrowser.js'],
  ['index.js.map', 'opendevbrowser.js.map'],
  ['index.d.ts', 'opendevbrowser.d.ts'],
  ['index.d.ts.map', 'opendevbrowser.d.ts.map'],
];
for (const [src, dst] of pairs) {
  const from = resolve(dist, src);
  const to = resolve(dist, dst);
  if (existsSync(from)) copyFileSync(from, to);
}"
